package com.config;import org.springframework.beans.factory.annotation.Qualifier;import org.springframework.boot.autoconfigure.jdbc.DataSourceProperties;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.Primary;import javax.sql.DataSource;@Configurationpublic class DateSourceConfig {    @Primary    @Bean("taikangDateSourceProperties")    @ConfigurationProperties(prefix = "spring.datasource.taikang")    public DataSourceProperties taikangDataSourceProperties(){        return new DataSourceProperties();    }    @Primary    @Bean("taikangDateSource")    public DataSource taikangDateSource(@Qualifier("taikangDateSourceProperties") DataSourceProperties dataSourceProperties){        return dataSourceProperties.initializeDataSourceBuilder().build();    }    @Bean("mpDataSourceProperties")    @ConfigurationProperties(prefix = "spring.datasource.mp")    public DataSourceProperties mpDataSourceProperties(){        return new DataSourceProperties();    }    @Bean("mpDataSource")    public DataSource mpDataSource(@Qualifier("mpDataSourceProperties") DataSourceProperties dataSourceProperties){        return dataSourceProperties.initializeDataSourceBuilder().build();    }}