package com.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Qualifier;import org.springframework.jdbc.core.JdbcTemplate;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;import java.util.Map;@RestControllerpublic class JdbcController {    @Autowired    @Qualifier("taiKangJdbc")    JdbcTemplate taiKangJdbc;    @Autowired    @Qualifier("mpJdbc")    JdbcTemplate mpJdbc;    @RequestMapping("taiKangJdbc")    public List<Map<String, Object>> taiKangJdbc(){        String sql = "SELECT * from person";        List<Map<String, Object>> maps = taiKangJdbc.queryForList(sql);        return maps;    }    @RequestMapping("mpJdbc")    public List<Map<String, Object>> mpJdbc(){        String sql = "SELECT * from consumer";        List<Map<String, Object>> stringObjectMap = mpJdbc.queryForList(sql);        return stringObjectMap;    }}