package com.controller;import org.springframework.core.io.ClassPathResource;import org.springframework.core.io.Resource;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.io.*;@RestControllerpublic class ReadMeController {    @RequestMapping("/readMe")    public String hello() throws Exception {        File file = new ClassPathResource("txtFile/readme.txt").getFile();        BufferedReader br = new BufferedReader(new FileReader(file));        String line = null;        while ((line = br.readLine())!=null){            System.out.println(line);        }        return "success read info";    }    @RequestMapping("/readMe2")    public String hello2() throws Exception {        Resource pathResource = new ClassPathResource("classpath:txtFile/readme.txt");        InputStream inputStream = pathResource.getInputStream();        BufferedReader bufferedReader = new BufferedReader(new InputStreamReader(inputStream));        String line;        while ((line = bufferedReader.readLine())!=null){            System.out.println(line);        }        return "read finish";    }    @RequestMapping("/readMe3")    public String hello3() throws Exception {        InputStream inputStream = this.getClass().getClassLoader().getResourceAsStream("classpath:txtFile/readme.txt");        BufferedReader bufferedReader = new BufferedReader(new InputStreamReader(inputStream));        String line;        while ((line = bufferedReader.readLine())!=null){            System.out.println(line);        }        return "read finish";    }}